export default function ProgressSection({ data = defaultData }) {
  const [percent, setPercent] = useState(0);

  useEffect(() => {
    // animate up to target percent
    const target = Number(data.percent || 0);
    let start = 0;
    const step = () => {
      start += Math.max(1, Math.round(target / 30));
      if (start >= target) {
        start = target;
        setPercent(start);
      } else {
        setPercent(start);
        requestAnimationFrame(step);
      }
    };
    const t = setTimeout(() => requestAnimationFrame(step), 300);
    return () => clearTimeout(t);
  }, [data.percent]);

  return (
    <section className="py-16 bg-gradient-to-b from-slate-900 to-slate-800">
      <style>{`
        .bento-grid {
          display: grid;
          grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
          gap: 20px;
          grid-auto-rows: auto;
        }

        .bento-item {
          background: linear-gradient(135deg, rgba(30, 41, 59, 0.5) 0%, rgba(15, 23, 42, 0.5) 100%);
          border: 1px solid rgba(100, 116, 139, 0.2);
          border-radius: 16px;
          padding: 24px;
          transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
          overflow: hidden;
          position: relative;
          backdrop-filter: blur(10px);
        }

        .bento-item::before {
          content: '';
          position: absolute;
          top: 0;
          left: 0;
